import React, { Component } from 'react';
import ProgressBar from './ProgressBar';

class Languages extends Component {

    state = {

        "languages": [
            { id: 1, value: "JAVA", xp: 1 },
            { id: 2, value: "CSS", xp: 2 },
            { id: 3, value: "PHP", xp: 1 },
            { id: 3, value: "Javascript", xp: 2 }

        ],

        "frameworks": [
            { id: 1, value: "React", xp: 0.3 },
            { id: 2, value: "laravel", xp: 0.3 },
            { id: 3, value: "Maven", xp: 0.3 }

        ]
    }
    render() {

        let { languages, frameworks } = this.state;
        return (
            <div className="languagesFrameworks">
                <ProgressBar
                    LaVariable={languages} //C'est la variable qui contient les donnés qu'on traitera
                    className="languagesDisplay"
                    title="languages"

                />

                <ProgressBar LaVariable={frameworks}
                    className="frameworksDisplay"
                    title="frameworks & bibliothèques"
                />

            </div>
        );
    }
}

export default Languages;
