import React from 'react';

const ProgressBar = (props) => {

    return (
        <div className={props.className}>

            <h3>{props.title}</h3>
            <div className="years">
                <span>Année d'Experiences</span>
                <span>1 an</span>
                <span>2 ans</span>
            </div>

            <div>
                {
                    props.LaVariable.map((item) => {
                        let xpYears = 2;
                        let progressBar = item.xp / xpYears * 100 + '%';//ca definit la taille de la barre en fonction de xp l'année definit en avance et le % est utilise pour pouvoir utiliser la variable apres dans le width qui doit etre en pourcent
                        return (
                            <div key={item.id} className='languagesList'>

                                <li>{item.value}</li>

                                <div className="progressBar" style={{ width: progressBar }} /**ici on a definit width c'est a dire la largeur en fonction de la la poucentage pour l'utiliser apres dans le style**/>


                                </div>
                            </div>
                        )
                    }
                    )
                }
            </div>
        </div>
    );
};

export default ProgressBar;
